     , (5695, 6 /* Give_EmoteCategory */, 1, 5, 3 /* Give_EmoteType */, 2, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 5883 /* Gelidite Standing Stone Translation */, 1, 0, 0, False, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (5695, 6 /* Give_EmoteCategory */, 1, 6, 10 /* Tell_EmoteType */, 0, 1, NULL, 'Be warned, though - you will need a substantial party to reach the Lost City, and should you die...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
     , (5695, 6 /* Give_EmoteCategory */, 1, 7, 10 /* Tell_EmoteType */, 0, 1, NULL, 'You will be unable to reenter the caves to recover your equipment. Be certain you are prepared before you ask Mairisa to cast the portal!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL)
